import React from "react";

const EXAM_LABELS = ["第一次", "第二次", "第三次", "第四次"];

const ScoreList = ({ scores, onEdit, onDelete }) => {
  return (
    <div className="overflow-x-auto mt-6">
      <table className="min-w-full border rounded shadow bg-white">
        <thead>
          <tr>
            <th className="border px-4 py-2">学生姓名</th>
            {EXAM_LABELS.map((label, idx) => (
              <th key={idx} className="border px-4 py-2">{label}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {scores.length === 0 && (
            <tr>
              <td colSpan={5} className="text-center text-gray-400 py-4">暂无成绩</td>
            </tr>
          )}
          {scores.map((student) => (
            <tr key={student.studentName}>
              <td className="border px-4 py-2 font-medium">{student.studentName}</td>
              {Array.from({ length: 4 }).map((_, examIdx) => (
                <td key={examIdx} className="border px-2 py-1 text-center">
                  {student.scores[examIdx] !== undefined ? (
                    <div className="flex items-center justify-center gap-2">
                      <span>{student.scores[examIdx]}</span>
                      <button
                        className="text-xs text-blue-600 underline hover:text-blue-800"
                        aria-label={`编辑${student.studentName}第${examIdx + 1}次成绩`}
                        tabIndex={0}
                        onClick={() => onEdit({
                          studentName: student.studentName,
                          examIndex: examIdx,
                          score: student.scores[examIdx],
                        })}
                        onKeyDown={(e) => e.key === 'Enter' && onEdit({
                          studentName: student.studentName,
                          examIndex: examIdx,
                          score: student.scores[examIdx],
                        })}
                      >编辑</button>
                      <button
                        className="text-xs text-red-600 underline hover:text-red-800"
                        aria-label={`删除${student.studentName}第${examIdx + 1}次成绩`}
                        tabIndex={0}
                        onClick={() => onDelete({
                          studentName: student.studentName,
                          examIndex: examIdx,
                        })}
                        onKeyDown={(e) => e.key === 'Enter' && onDelete({
                          studentName: student.studentName,
                          examIndex: examIdx,
                        })}
                      >删除</button>
                    </div>
                  ) : (
                    <span className="text-gray-300">--</span>
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ScoreList;
