import React, { useState, useEffect } from "react";

const EXAM_LABELS = ["第一次", "第二次", "第三次", "第四次"];

const ScoreEntryForm = ({ onSubmit, editing, initialData, onCancel }) => {
  const [studentName, setStudentName] = useState("");
  const [examIndex, setExamIndex] = useState(0);
  const [score, setScore] = useState("");

  useEffect(() => {
    if (editing && initialData) {
      setStudentName(initialData.studentName);
      setExamIndex(initialData.examIndex);
      setScore(initialData.score);
    } else {
      setStudentName("");
      setExamIndex(0);
      setScore("");
    }
  }, [editing, initialData]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!studentName.trim() || score === "") return;
    onSubmit({ studentName: studentName.trim(), examIndex, score: Number(score) });
    if (!editing) {
      setStudentName("");
      setScore("");
    }
  };

  return (
    <form className="flex flex-col gap-2 p-4 border rounded bg-white shadow max-w-md mx-auto" onSubmit={handleSubmit}>
      <div className="flex flex-col gap-1">
        <label className="font-medium">学生姓名</label>
        <input
          className="border rounded px-2 py-1 focus:outline-none focus:ring"
          type="text"
          value={studentName}
          onChange={(e) => setStudentName(e.target.value)}
          aria-label="学生姓名"
          required
          disabled={editing}
        />
      </div>
      <div className="flex flex-col gap-1">
        <label className="font-medium">考试轮次</label>
        <select
          className="border rounded px-2 py-1 focus:outline-none focus:ring"
          value={examIndex}
          onChange={(e) => setExamIndex(Number(e.target.value))}
          aria-label="考试轮次"
          required
          disabled={editing}
        >
          {EXAM_LABELS.map((label, idx) => (
            <option key={idx} value={idx}>{label}</option>
          ))}
        </select>
      </div>
      <div className="flex flex-col gap-1">
        <label className="font-medium">成绩</label>
        <input
          className="border rounded px-2 py-1 focus:outline-none focus:ring"
          type="number"
          value={score}
          onChange={(e) => setScore(e.target.value)}
          aria-label="成绩"
          required
          min={0}
          max={150}
        />
      </div>
      <div className="flex gap-2 mt-2">
        <button type="submit" className="bg-blue-600 text-white rounded px-4 py-1 hover:bg-blue-700">{editing ? "保存" : "录入"}</button>
        {editing && (
          <button type="button" className="bg-gray-300 rounded px-4 py-1 hover:bg-gray-400" onClick={onCancel}>取消</button>
        )}
      </div>
    </form>
  );
};

export default ScoreEntryForm;
