import React from 'react';

const TabSwitcher = ({ activeTab, onTabChange }) => {
  return (
    <div style={{ display: 'flex', justifyContent: 'center', margin: '1rem 0' }}>
      <button
        onClick={() => onTabChange('damage')}
        style={{
          background: 'none',
          border: 'none',
          outline: 'none',
          marginRight: 24,
          opacity: activeTab === 'damage' ? 1 : 0.5,
          transform: activeTab === 'damage' ? 'scale(1.2)' : 'scale(1)',
          transition: 'all 0.2s',
          cursor: 'pointer',
        }}
        title="伤害榜"
      >
        {/* 卡通奖杯icon */}
        <img src="https://img.icons8.com/color/48/000000/trophy.png" alt="榜" style={{ width: 40, height: 40 }} />
      </button>
      <button
        onClick={() => onTabChange('input')}
        style={{
          background: 'none',
          border: 'none',
          outline: 'none',
          opacity: activeTab === 'input' ? 1 : 0.5,
          transform: activeTab === 'input' ? 'scale(1.2)' : 'scale(1)',
          transition: 'all 0.2s',
          cursor: 'pointer',
        }}
        title="录入成绩"
      >
        {/* 卡通铅笔icon */}
        <img src="https://img.icons8.com/color/48/000000/pencil--v1.png" alt="录入" style={{ width: 40, height: 40 }} />
      </button>
    </div>
  );
};

export default TabSwitcher;
