import React, { useState } from "react";
import ScoreEntryForm from "./components/ScoreEntryForm";
import ScoreList from "./components/ScoreList";

const MAX_EXAMS = 4;

const App = () => {
  // scores: [{ studentName: string, scores: number[MAX_EXAMS] }]
  const [scores, setScores] = useState([]);
  const [editing, setEditing] = useState(null); // {studentName, examIndex, score}

  // 新增或更新成绩
  const handleAddOrUpdateScore = ({ studentName, examIndex, score }) => {
    setScores((prev) => {
      const idx = prev.findIndex((s) => s.studentName === studentName);
      if (idx === -1) {
        // 新学生
        const newScores = Array(MAX_EXAMS).fill(undefined);
        newScores[examIndex] = score;
        return [...prev, { studentName, scores: newScores }];
      } else {
        // 已有学生，更新对应考试成绩
        const updated = [...prev];
        const stu = { ...updated[idx], scores: [...updated[idx].scores] };
        stu.scores[examIndex] = score;
        updated[idx] = stu;
        return updated;
      }
    });
    setEditing(null);
  };

  // 删除单次成绩
  const handleDeleteScore = ({ studentName, examIndex }) => {
    setScores((prev) => {
      const idx = prev.findIndex((s) => s.studentName === studentName);
      if (idx === -1) return prev;
      const updated = [...prev];
      const stu = { ...updated[idx], scores: [...updated[idx].scores] };
      stu.scores[examIndex] = undefined;
      updated[idx] = stu;
      return updated;
    });
    setEditing(null);
  };

  // 编辑成绩
  const handleEditScore = (editInfo) => {
    setEditing(editInfo);
  };

  // 取消编辑
  const handleCancelEdit = () => setEditing(null);

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-blue-200 flex flex-col items-center py-8">
      <h1 className="text-3xl font-bold mb-4 text-blue-800">班级勇士打BOSS成绩榜</h1>
      <div className="w-full max-w-2xl">
        <ScoreEntryForm
          onSubmit={handleAddOrUpdateScore}
          editing={!!editing}
          initialData={editing}
          onCancel={handleCancelEdit}
        />
        <ScoreList
          scores={scores}
          onEdit={handleEditScore}
          onDelete={handleDeleteScore}
        />
      </div>
    </div>
  );
};

export default App;
